import React, { useState, useEffect, useCallback } from "react";
import Container from "react-bootstrap/Container";
import { Jumbotron } from "./migration";
import Row from "react-bootstrap/Row";
import ProjectCard from "./ProjectCard";
import axios from "axios";

const dummyProject = {
  name: null,
  description: null,
  svn_url: null,
  stargazers_count: null,
  languages_url: null,
  pushed_at: null,
};
const API = "https://api.github.com";

const Project = ({ heading, username, length, specfic }) => {
  const allReposAPI = `${API}/users/${username}/repos?sort=updated&direction=desc`;
  const specficReposAPI = `${API}/repos/${username}`;
  const dummyProjectsArr = new Array(length + specfic.length).fill(
    dummyProject
  );

  const [projectsArray, setProjectsArray] = useState([]);

  const fetchRepos = useCallback(async () => {
    let repoList = [];
    try {
      // Fetching all repos
      const response = await axios.get(allReposAPI);
      repoList = [...response.data.slice(0, length)];
      // Adding specific repos
      for (let repoName of specfic) {
        const response = await axios.get(`${specficReposAPI}/${repoName}`);
        repoList.push(response.data);
      }
      setProjectsArray(repoList);
    } catch (error) {
      console.error(error.message);
    }
  }, [allReposAPI, length, specfic, specficReposAPI]);

  useEffect(() => {
    fetchRepos();
  }, [fetchRepos]);

  return (
    <Jumbotron
      fluid
      id="projects"
      className="m-0"
      style={{
        backgroundColor: "#1e1e1e", // Fondo gris oscuro estilo programador
        color: "#ffffff", // Texto en blanco para mejor contraste
        padding: "50px 0",
      }}
    >
      <Container>
        <h2
          className="display-4 pb-5 text-center"
          style={{
            color: "#00d2ff", // Color de texto moderno similar al de MainBody
            fontFamily: "'Pixelify Sans', sans-serif", // Aplicar la misma tipografía
          }}
        >
          {heading}
        </h2>
        <Row>
          {projectsArray.length
            ? projectsArray.map((project, index) => (
                <ProjectCard
                  key={`project-card-${index}`}
                  id={`project-card-${index}`}
                  value={project}
                />
              ))
            : dummyProjectsArr.map((project, index) => (
                <ProjectCard
                  key={`dummy-${index}`}
                  id={`dummy-${index}`}
                  value={project}
                />
              ))}
        </Row>
      </Container>
    </Jumbotron>
  );
};

export default Project;
