import React, { useState, useEffect, useCallback } from "react";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Skeleton from "react-loading-skeleton";
import axios from "axios";

const ProjectCard = ({ value }) => {
  const {
    name,
    description,
    svn_url,
    stargazers_count,
    languages_url,
    pushed_at,
  } = value;

  return (
    <Col md={6}>
      <Card
        className="shadow-lg p-3 mb-5"
        style={{
          backgroundColor: "#1e1e1e", // Fondo gris oscuro
          color: "#ffffff", // Texto blanco para contraste
          borderRadius: "10px",
        }}
      >
        <Card.Body>
          <Card.Title
            as="h5"
            style={{
              color: "#00d2ff", // Mantiene la consistencia de color con MainBody
              fontFamily: "'Pixelify Sans', sans-serif", // Aplicar la misma tipografía
            }}
          >
            {name || <Skeleton />}
          </Card.Title>
          <Card.Text
            style={{
              color: "#b0bec5", // Color de texto suave
              fontFamily: "'Pixelify Sans', sans-serif",
            }}
          >
            {!description ? "" : description || <Skeleton count={3} />}
          </Card.Text>
          {svn_url ? <CardButtons svn_url={svn_url} /> : <Skeleton count={2} />}
          <hr />
          {languages_url ? (
            <Language languages_url={languages_url} repo_url={svn_url} />
          ) : (
            <Skeleton count={3} />
          )}
          {value ? (
            <CardFooter
              star_count={stargazers_count}
              repo_url={svn_url}
              pushed_at={pushed_at}
            />
          ) : (
            <Skeleton />
          )}
        </Card.Body>
      </Card>
    </Col>
  );
};

const CardButtons = ({ svn_url }) => {
  return (
    <div className="d-grid gap-2 d-md-block">
      <a
        href={`${svn_url}/archive/master.zip`}
        className="btn btn-outline-light mx-2"
        style={{ fontFamily: "'Pixelify Sans', sans-serif" }}
      >
        <i className="fab fa-github" /> Clone Project
      </a>
      <a
        href={svn_url}
        target="_blank"
        rel="noopener noreferrer"
        className="btn btn-outline-light mx-2"
        style={{ fontFamily: "'Pixelify Sans', sans-serif" }}
      >
        <i className="fab fa-github" /> Repo
      </a>
    </div>
  );
};

const Language = ({ languages_url, repo_url }) => {
  const [data, setData] = useState([]);

  const handleRequest = useCallback(async () => {
    try {
      const response = await axios.get(languages_url);
      setData(response.data);
    } catch (error) {
      console.error(error.message);
    }
  }, [languages_url]);

  useEffect(() => {
    handleRequest();
  }, [handleRequest]);

  const array = [];
  let total_count = 0;
  for (let index in data) {
    array.push(index);
    total_count += data[index];
  }

  return (
    <div className="pb-3">
      Languages:{" "}
      {array.length
        ? array.map((language) => (
            <a
              key={language}
              className="card-link"
              href={repo_url + `/search?l=${language}`}
              target="_blank"
              rel="noopener noreferrer"
              style={{
                color: "#b0bec5", // Color suave para consistencia visual
                fontFamily: "'Pixelify Sans', sans-serif",
              }}
            >
              <span className="badge bg-dark text-light">
                {language}:{" "}
                {Math.trunc((data[language] / total_count) * 1000) / 10} %
              </span>
            </a>
          ))
        : "code yet to be deployed."}
    </div>
  );
};

const CardFooter = ({ star_count, repo_url, pushed_at }) => {
  const [updated_at, setUpdated_at] = useState("0 mins");

  const handleUpdatetime = useCallback(() => {
    const date = new Date(pushed_at);
    const nowdate = new Date();
    const diff = nowdate.getTime() - date.getTime();
    const hours = Math.trunc(diff / 1000 / 60 / 60);

    if (hours < 24) {
      if (hours < 1) return setUpdated_at("just now");
      let measurement = hours === 1 ? "hour" : "hours";
      return setUpdated_at(`${hours.toString()} ${measurement} ago`);
    } else {
      const options = { day: "numeric", month: "long", year: "numeric" };
      const time = new Intl.DateTimeFormat("en-US", options).format(date);
      return setUpdated_at(`on ${time}`);
    }
  }, [pushed_at]);

  useEffect(() => {
    handleUpdatetime();
  }, [handleUpdatetime]);

  return (
    <p
      className="card-text"
      style={{ color: "#b0bec5", fontFamily: "'Pixelify Sans', sans-serif" }}
    >
      <a
        href={repo_url + "/stargazers"}
        target="_blank"
        rel="noopener noreferrer"
        className="text-light text-decoration-none"
      >
        <span className="card-link mr-4">
          <i className="fab fa-github" /> Stars{" "}
          <span className="badge badge-dark">{star_count}</span>
        </span>
      </a>
      <small className="text-muted">Updated {updated_at}</small>
    </p>
  );
};

export default ProjectCard;
